import 'package:flutter/material.dart';
import 'package:pratice/story_page.dart';
import 'package:story_view/story_view.dart';

class StoryViewPage extends StatefulWidget {
  @override
  State<StoryViewPage> createState() => _StoryViewPageState();
}

class _StoryViewPageState extends State<StoryViewPage> {
  final  controller = StoryController();

  List<StoryItem> StoryItems = [];

   @override
   void initState(){
   StoryItems = [
    StoryItem.pageImage(url: "data:image/jpeg;base64,", 
    controller: controller
    ),
  
   ];
   super.initState();
   }

  @override



  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title:const Text('StoryView Example'),
      ),
      body: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        crossAxisAlignment: CrossAxisAlignment.center,
        mainAxisSize: MainAxisSize.max,
        children: [
          GestureDetector(
            onTap: (){
              Navigator.of(context).push(MaterialPageRoute(builder: (context)=> StoryScreen(
                controller:controller,StoryItems:StoryItems
              ),
              ),
              );
            },
            child:const Center(
              child: CircleAvatar(
                radius: 30,
                backgroundColor: Colors.amber,
              )
            ),
          )
        ],
      )
    );
  }
}
